    public static SpannableStringBuilder getBoldStringBuilder(String header, String content) {
        SpannableStringBuilder stringBuilder = new SpannableStringBuilder(header + "\n\n" + content);
        StyleSpan boldStyleSpan = new StyleSpan(android.graphics.Typeface.BOLD);
        stringBuilder.setSpan(boldStyleSpan, 0, header.length(), Spannable.SPAN_INCLUSIVE_INCLUSIVE);
        return stringBuilder;
    }

    public static String capitalizeString(String string) {
        char[] chars = string.toLowerCase().toCharArray();
        boolean found = false;
        for (int i = 0; i < chars.length; i++) {
            if (!found && Character.isLetter(chars[i])) {
                chars[i] = Character.toUpperCase(chars[i]);
                found = true;
            } else if (Character.isWhitespace(chars[i]) || chars[i]=='-' || chars[i]=='.' || chars[i]=='\'') {
                found = false;
            }
        }
        return String.valueOf(chars);
    }

    public static String urlify(String s) {
        char[] toReturn = new char[s.length() * 2];
        int usedSpace = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == ' ') {
                toReturn[i + usedSpace] = '%';
                usedSpace++;
                toReturn[i + usedSpace] = '2';
                usedSpace++;
                toReturn[i + usedSpace] = '0';
                while (s.charAt(i + 1) == ' ') {
                    i++;
                    usedSpace--;
                }
                continue;
            }
            toReturn[i + usedSpace] = s.charAt(i);
        }
        String url = String.valueOf(toReturn);
        Log.i(TAG, url);
        return url.substring(0, url.indexOf(' ') - 1);
    }
